#!/bin/bash

function fail() {
	echo "${@}"
	exit 1
}

# The host running the http application (from which the user is going to connect)
DJIF_HOST="localhost"
# The host running SGBD
DB_HOST="localhost"
# Mysql default is 3306
DB_PORT="3306"
DB_NAME=""
DB_USER=""
DB_PASS=""
CONTACT=""

echo "Choose a name for the mysql database storing the data (default 'djif')"
read DB_NAME
if [ -z "${DB_NAME}" ]
then
	echo "No value entered, defaulting to 'dijf'"
	DB_NAME="djif"
fi
echo "Choose a name for the mysql user owning them"
read DB_USER
if [ -z "${DB_USER}" ]
then
	fail "Variable DB_USER is not defined."
fi
stty -echo
echo "Choose a password to authenticate this user (and remember it well)"
read DB_PASS
echo "Please confirm the chosen password"
read DB_PASS_BIS
stty echo
if [ "${DB_PASS}" != "${DB_PASS_BIS}" ]
then
	fail "Failed to confirm password, aborting"
fi
if [ -z "${DB_PASS}" ]
then
	fail "Variable DB_PASS is not defined."
fi
echo "The users might try to contact you, which email address do you want them to use ?"
read CONTACT
if [ -z "${CONTACT}" ]
then
	fail "No email address was provided"
fi

CONFIGDIR="config"

if [ -z "${DJIF_HOST}" ]
then
	fail "Variable DJIF_HOST is not defined."
fi
if [ -z "${DB_HOST}" ]
then
	fail "Variable DB_HOST is not defined."
fi
if [ -z "${DB_PORT}" ]
then
	fail "Variable DB_PORT is not defined."
fi

cat > "${CONFIGDIR}/config.php" <<EOF
<?php
define('DB_HOST','${DB_HOST}');
define('DB_PORT','${DB_PORT}');
define('DB_NAME','${DB_NAME}');
define('DB_USER','${DB_USER}');
define('DB_PASS','${DB_PASS}');
define('CONTACT', '${CONTACT}');
?>
EOF

echo "Mysql root password is required to create the database and user required to run Djif"
mysql -u root -h ${DB_HOST} -P ${DB_PORT} -p <<EOF
DROP DATABASE IF EXISTS \`${DB_NAME}\`;
CREATE DATABASE \`${DB_NAME}\`;
USE \`${DB_NAME}\`;

CREATE TABLE \`types\` (
	\`id\` tinyint NOT NULL,
	\`name\` VARCHAR(10),
	PRIMARY KEY (\`id\`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE \`media\` (
	\`id\` int NOT NULL,
	\`type\` tinyint NOT NULL,
	\`url\` text NOT NULL,
	\`width\` smallint,
	\`height\` smallint,
	PRIMARY KEY (\`id\`),
	FOREIGN KEY (\`type\`) REFERENCES \`types\`(\`id\`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE \`djifs\` (
  \`hash\` char(5) CHARACTER SET ascii NOT NULL,
  \`gif\` int NOT NULL,
  \`audio\` int NOT NULL,
  \`ip\` int(11) DEFAULT NULL,
  \`date\` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  \`visits\` int(11) DEFAULT '0',
	\`preview\` blob NOT NULL,
  PRIMARY KEY (\`hash\`),
	FOREIGN KEY (\`gif\`) REFERENCES \`media\`(\`id\`),
	FOREIGN KEY (\`audio\`) REFERENCES \`media\`(\`id\`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

GRANT INSERT, SELECT, UPDATE ON \`${DB_NAME}\`.* TO \`${DB_USER}\`@\`${DJIF_HOST}\` IDENTIFIED BY '${DB_PASS}';

LOCK TABLES \`types\` WRITE, \`media\` WRITE, \`djifs\` WRITE;

INSERT INTO \`types\` VALUES
	(0, 'gif'),
	(1, 'audiofile'),
	(2, 'youtube');
INSERT INTO \`media\` VALUES
	(1, 0, 'http://localhost/images/first.gif', 281, 162),
	(2, 2, 'https://www.youtube.com/watch?v=WaIJKM0sjdo&t=12s', 300, 300);
INSERT INTO \`djifs\` VALUES
	('first', 1, 2, 2130706433, '2013-04-29 12:19:47', 0, X'');

UNLOCK TABLES;

EOF
